import CartContext from "../../store/cart-context";
import AuthContext from "../../store/auth-context";
import CartModalItem from "./CartModalItem";
import React, { useContext, useState } from "react";
import Modal from "./Modal";
import classes from "./CartModal.module.css";
import Checkout from "./Checkout";
import { useHistory } from "react-router";
import {useStripe} from "@stripe/react-stripe-js";

let graphqlQuery;
let response;
let arrayItems;
let firstTime;
let newArrayItems;

const CartModal = (props) => {
  firstTime = false;
  const stripe = useStripe();
  const [isCheckingout, setIsCheckingout] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [products, setProducts] = useState(false);
  const history = useHistory();
  
  console.log(isSending);
  console.log("Inside CartModal");
  const cartCtx = useContext(CartContext);
  const authCtx = useContext(AuthContext);
  console.log(authCtx.token);
  console.log(cartCtx.items);
  graphqlQuery = {
    query: ` query {
          getCart(id:authCtx.userId) {
    products{
      product{
        title
        price
        _id
        imageUrl
      }
      quantity
    }
  }   
               
          }`,
  };
  const firstTimeFunction = async (graphqlQuery) => {
    console.log(graphqlQuery);
    const jsonResponse = await fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(graphqlQuery),
      headers: {
        Authorization: "Bearer " + authCtx.token,
        "Content-Type": "application/json",
      },
    })
    .then((res) => {
      return res.json();
    });

    // if (firstTime){
    //   console.log(jsonResponse);
    //   arrayItems = jsonResponse.data.getCart.products;
    //   console.log(arrayItems);
    //   console.log('Inside array items');
    //   newArrayItems = arrayItems.map(item=>{
    //    return {
    //      ...item.product,
    //      amount:item.quantity,
    //      id:item.product._id,
    //      name:item.product.title,
    //      image:item.product.imageUrl
    //    }
    //  });
    //  console.log(newArrayItems);
    //   firstTime = false;
      

    // }
  };

  if (!products){
    firstTime = true;
    firstTimeFunction(graphqlQuery);
    
  }
 

  const cartItemRemoveHandler = (id) => {
    console.log("Inside remove handler");
    graphqlQuery = {
      query: ` mutation {
          deleteProduct(id: "${id}") 
               
          }`,
    };
    firstTimeFunction(graphqlQuery);
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    console.log(item);
    graphqlQuery = {
      query: ` mutation {
          addProduct(id: "${item.id}") 
               
          }`,
    };
    firstTimeFunction(graphqlQuery);
    cartCtx.addItem({
      id: item.id,
      name: item.name,
      amount: 1,
      price: +item.price,
      image: item.image,
    });
  };
  const orderHandler = () => {
    setIsCheckingout(true);
  };
  const timeOutHandler = () => {
    cartCtx.clearCart();
    history.replace("/");
  };
  const submitOrderHandler = async (userData) => {
    console.log('inside submit handler');
    const result = await fetch(
      "http://localhost:8000/getstripesession/5600",
      {
        method: 'POST',
        headers: {
          Authorization: 'Bearer ' + authCtx.token
        },
        body: JSON.stringify({
          amount:5600
        })
      }
    ).then(res=>{
    return res.json();
  });
  console.log('succesull stripe');
  console.log(result);
  console.log(userData);
  console.log(userData.paymentMethodId);

  const confirmPayment = await stripe.confirmCardPayment(result,{
    payment_method:userData.paymentMethodId,
  });
  console.log(confirmPayment);
  if (confirmPayment.paymentIntent.status == 'succeeded'){
    console.log('inside succeeded');
    const graphqlQuery = {
      query: ` mutation {
          postOrder          
          }`,
    };
    const jsonResponse = await fetch("http://localhost:8000/graphql", {
    method: "POST",
    body: JSON.stringify(graphqlQuery),
    headers: {
      Authorization: "Bearer " + authCtx.token,
      "Content-Type": "application/json",
    },
  })
  .then((res) => {
    return res.json();
  });

  
  setIsSending(true);

  const timer = setTimeout(timeOutHandler, 200000);
  }

  };
  return (
    <Modal>
      {!isSending && (
        <ul className={classes["cart-items"]}>
          {cartCtx.items &&
            cartCtx.items.map((item) => (
              <CartModalItem
                price={item.price}
                amount={item.amount}
                name={item.name}
                onRemove={cartItemRemoveHandler.bind(null, item.id)}
                onAdd={cartItemAddHandler.bind(null, item)}
              />
            ))}
        </ul>
      )}
      {cartCtx.items.length > 0 && !isSending && (
        <React.Fragment>
          <div className={classes.totAmount}>
            <span>Total Amount:</span>
            <span>${cartCtx.totalAmount}</span>
          </div>
          {!isCheckingout && !isSending && (
            <div className={classes.actionflex}>
              <button onClick={props.onClose}>Cancel</button>
              <button onClick={orderHandler}>Checkout</button>
            </div>
          )}
        </React.Fragment>
      )}
      {isCheckingout && !isSending && (
        <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose} totalAmount={cartCtx.totalAmount} />
      )}
      {isSending && (
        <div className={classes.sending}>
          <img src="https://media0.giphy.com/media/dAzkOoCgoFHtCAdFhe/200w.webp?cid=ecf05e47evrfhegpbmfzuhfehn0kcljuh9oc7oybsjmpblvw&rid=200w.webp&ct=g" />
          <p> Your order has been processed!</p>
        </div>
      )}
    </Modal>
  );
};
export default CartModal;
