import { useContext, Fragment, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CartModal from "./CartModal";

import AuthContext from "../../store/auth-context";
import classes from "./MainNavigation.module.css";
import CartIcon from "./CartIcon";

import CartContext from "../../store/cart-context";

const MainNavigation = () => {
  console.log("Inside MN");
  const [highlighted, setHighlighted] = useState(false);
  const authCtx = useContext(AuthContext);
  const CartCtx = useContext(CartContext);
  console.log(CartCtx.items.length);
  useEffect(() => {
    console.log("Inside useffect");
    setHighlighted(true);

    const timer = setTimeout(() => {
      setHighlighted(false);
    }, 1000);
    return () => {
      clearTimeout(timer);
    };
  }, [CartCtx.items.length]);
  console.log(highlighted);
  const cartNumclasses = `${classes.badge} ${highlighted ? classes.bump : ""}`;
  console.log(cartNumclasses);
  const showCartHandler = () => {
    console.log("inside main navigation handler");
    CartCtx.toggleCart();
  };
  const closeCartHandler = () => {
    console.log("inside main navigation handler or close");
    CartCtx.toggleCart();
  };
  const isLoggedIn = authCtx.isLoggedIn;
  console.log("Again inside Mian Navigation");
  console.log(CartCtx.showCart);

  const logoutHandler = () => {
    authCtx.logout();
    // optional: redirect the user
  };

  return (
    <Fragment>
      {authCtx.isLoggedIn && CartCtx.showCart && (
        <CartModal onClose={closeCartHandler} />
      )}
      <header className={classes.header}>
        <Link to="/">
          <div className={classes.logodiv}>
            <img src="https://cdn-icons-png.flaticon.com/512/45/45332.png" />
            <div className={classes.logo}>Food Truck</div>
          </div>
        </Link>
        <nav>
          <ul className={classes.ulClass}>
            {!isLoggedIn && (
              <li>
                <Link to="/auth">Login</Link>
              </li>
            )}
            {isLoggedIn && (
              <button onClick={showCartHandler}>
                <span className={classes.icon}>
                  <CartIcon />
                </span>
                <span className={classes.heading}>Cart</span>
                <span className={cartNumclasses}>{CartCtx.items.length}</span>
              </button>
            )}

            {isLoggedIn && (
              <li>
                <Link className={classes.liMenu} to="/profile">
                  Profile
                </Link>
                <ul>
                  <li>
                    <Link className={classes.insideLink} to="/previousOrders">
                      Previous Orders
                    </Link>
                  </li>
                  <li>
                    <button
                      className={classes.insideLink}
                      onClick={logoutHandler}
                    >
                      Logout
                    </button>
                  </li>
                </ul>
              </li>
            )}
          </ul>
        </nav>
      </header>
    </Fragment>
  );
};

export default MainNavigation;
