import React from "react";

const CartContext = React.createContext({
  items: [],
  totalAmount: 0,
  showCart: false,

  addItem: (item) => {},
  removeItem: (id) => {},
  clearCart: () => {},
  toggleCart: () => {},
});

export default CartContext;
