import PreviousOrderItem from "./PreviousOrderItem";
import classes from "./PreviousOrders.module.css";
import { useState, useContext } from "react";
import AuthContext from "../../store/auth-context";

let orderItem;
let fetchData;
let noOfPages = 1;

let startIndx;
let stopIndx;
const PreviousOrders = () => {
  const authCtx = useContext(AuthContext);
  const [currentPage, setCurrentPage] = useState(1);

  const pageHandler = (e) => {
    console.log("Inside page handler");
    const target = e.target;
    const span = target.dataset.span;


    console.log(target);
    console.log(span);
    if (+span === 0) {
      setCurrentPage(currentPage - 1);
    } else if (+span === 1) {
      setCurrentPage(currentPage + 1);
    }
  };
  console.log(currentPage);
  startIndx = (currentPage - 1) * 2;
  stopIndx = startIndx + 2;
  console.log(authCtx.userId);

  const [fetchData, setFetchData] = useState(false);
  const graphqlQuery = {
    query: ` query {
          getPreviousOrders(id: "${authCtx.userId}") {
            orders{
              _id
              products{
                product{
                  _id
                  title
                  imageUrl
                  price
                       }
                quantity
                       }
            }
    
  }   
               
          }`,
  };
  const fetchOrders = async function () {
    console.log(graphqlQuery);
    const data = await fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(graphqlQuery),
      headers: {
        Authorization: "Bearer " + authCtx.token,
        "Content-Type": "application/json",
      },
    });

    const response = await data.json();

    const arrayObj = response.data.getPreviousOrders.orders;
    console.log(arrayObj);
    orderItem = arrayObj.map((item, i) => {
      console.log(item.products[0].quantity);
      const totalAmount = item.products.reduce((acc,curr)=>{
        return acc + ( curr.product.price * curr.quantity)
      },0)
      return {
        id: item._id,
        items: item.products,
        totalAmount,
      };
    });

    console.log(orderItem);
    noOfPages = Math.ceil(orderItem.length / 2);
    console.log(noOfPages);
    setFetchData(true);
  };
  fetchOrders();
  return (
    <div className={classes.mainDiv}>
      <h5>Order History</h5>
      <ul>
        {fetchData &&
          orderItem
            .slice(startIndx, stopIndx)
            .map((item, ind) => (
              <PreviousOrderItem
                list={item}
                indx={(currentPage - 1) * 2 + ind + 1}
              />
            ))}
      </ul>
      {fetchData && (
        <div className={classes.btnGrp}>
          {currentPage > 1 && (
            <button onClick={pageHandler} data-span={0}>
              Previous
            </button>
          )}
          <button>{currentPage}</button>

          {currentPage < noOfPages && (
            <button onClick={pageHandler} data-span={1}>
              Next
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default PreviousOrders;
